import { useState } from "react";
import {
  Box,
  Button,
  TextField,
  useMediaQuery,
  Typography,
  useTheme,
} from "@mui/material";
import EditOutlinedIcon from "@mui/icons-material/EditOutlined";
import { Formik } from "formik";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { setLogin } from "state";
import DropZone from "react-dropzone";
import FlexBetween from "components/FlexBetween";

const registerSchema = yup.object().shape({
  usn: yup.string().required("required"),
  firstName: yup.string().required("required"),
  lastName: yup.string().required("required"),
  branch: yup.string().required("required"),
  sem: yup.number().required("required"),
  email: yup.string().email("invalid email").required("required"),
  password: yup.string().required("required"),
  phoneNumber: yup.string().required("required"),
  gender: yup.string().required("required"),
  picture: yup.string().required("required"),
});

const loginSchema = yup.object().shape({
  email: yup.string().email("invalid email").required("required"),
  password: yup.string().required("required"),
});

const initialValuesRegister = {
  usn : "",
  firstName: "",
  lastName: "",
  branch:"",
  sem:"",
  email: "",
  password: "",
  phoneNumber: "",
  gender: "",
  picture: "",
};

const initialValuesLogin = {
  email: "",
  password: "",
};

const Form = () => {
  const [pageType, setPageType] = useState("login");
  const { palette } = useTheme();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const isNonMobile = useMediaQuery("(min-width : 600px)");
  const isLogin = pageType === "login";
  const isRegister = pageType === "register";

  const register = async (values, onSubmitProps)=>{
    //All the data sent through the form is present in values but since we have image separately we cannot directly use it .
    //This allows us to send form info with image.
    const formData = new FormData();
    for(let value in values) {
        formData.append(value,values[value]);
    }
    formData.append('picturePath',values.picture.name);

    const savedUserResponse = await fetch(
        "http://localhost:3001/auth/register",
        {
            method : 'POST',
            body : formData,
        }
    );
    const savedUser = await savedUserResponse.json();
    onSubmitProps.resetForm();

    if (savedUser) {
        setPageType("login");
    }
  };

  const login= async ( values, onSubmitProps ) => {
    const loggedInResponse = await fetch(
        "http://localhost:3001/auth/login",
        {
            method : 'POST',
            headers : {"Content-Type" : "application/json"},
            body : JSON.stringify(values),
        }
    );
    const loggedIn = await loggedInResponse.json();
    onSubmitProps.resetForm();

    if(loggedIn) {
        dispatch(
            setLogin({
                user : loggedIn.user,
                token : loggedIn.token
            })
        );
        navigate("/home");
    }
  };

  const handleFormSubmit = async (values, onSubmitProps) => {
    if (isLogin) await login(values, onSubmitProps);
    if(isRegister) await register(values,onSubmitProps);
  };

  return (
    <Formik
      onSubmit={handleFormSubmit}
      initialValues={isLogin ? initialValuesLogin : initialValuesRegister}
      validationSchema={isLogin ? loginSchema : registerSchema}
    >
      {({
        values,
        errors,
        touched,
        handleBlur,
        handleChange,
        handleSubmit,
        setFieldValue,
        resetForm,
      }) => (
        <form onSubmit={handleSubmit}>
          <Box
            display="grid"
            gap="30px"
            gridTemplateColumns="repeat(4,minmax(0,1fr))"
            sx={{
              "& > div": { gridColumn: isNonMobile ? undefined : "span 4" },
            }}
          >
            {isRegister && (
              <>
              <TextField
                  label="USN"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.usn}
                  name="usn"
                  error={
                    Boolean(touched.usn) && Boolean(errors.usn)
                  }
                  helperText={touched.usn && errors.usn}
                  sx={{ gridColumn: "span 2" }}
                />
                <TextField
                  label="First Name"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.firstName}
                  name="firstName"
                  error={
                    Boolean(touched.firstName) && Boolean(errors.firstName)
                  }
                  helperText={touched.firstName && errors.firstName}
                  sx={{ gridColumn: "span 2" }}
                />
                <TextField
                  label="Last Name"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.lastName}
                  name="lastName"
                  error={Boolean(touched.lastName) && Boolean(errors.lastName)}
                  helperText={touched.lastName && errors.lastName}
                  sx={{ gridColumn: "span 2" }}
                />
                <TextField
                  label="Branch"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.branch}
                  name="branch"
                  error={
                    Boolean(touched.branch) && Boolean(errors.branch)
                  }
                  helperText={touched.branch && errors.branch}
                  sx={{ gridColumn: "span 2" }}
                />
                <TextField
                  label="Sem"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.sem}
                  name="sem"
                  error={Boolean(touched.sem) && Boolean(errors.sem)}
                  helperText={touched.sem && errors.sem}
                  sx={{ gridColumn: "span 4" }}
                />
                <TextField
                  label="Phone Number"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.phoneNumber}
                  name="phoneNumber"
                  error={
                    Boolean(touched.phoneNumber) && Boolean(errors.phoneNumber)
                  }
                  helperText={touched.phoneNumber && errors.phoneNumber}
                  sx={{ gridColumn: "span 4" }}
                />
                <TextField
                  label="Gender"
                  onBlue={handleBlur}
                  onChange={handleChange}
                  value={values.gender}
                  name="gender"
                  error={
                    Boolean(touched.gender) && Boolean(errors.gender)
                  }
                  helperText={touched.gender && errors.gender}
                  sx={{ gridColumn: "span 2" }}
                />
                <Box
                  gridColumn="span 4"
                  border={`1px solid ${palette.neutral.medium}`}
                  borderRadius="5px"
                  p="1rem"
                >
                  <DropZone
                    acceptedFiles=".jpg,.jpeg,.png"
                    multiple={false}
                    onDrop={(acceptedFiles) =>
                      setFieldValue("picture", acceptedFiles[0])
                    }
                  >
                    {({ getRootProps, getInputProps }) => (
                      <Box
                        {...getRootProps()}
                        border={`2px dashed ${palette.primary.main}`}
                        p="1rem"
                        sx={{ "&:hover": { cursor: "pointer" } }}
                      >
                        <input {...getInputProps()} />
                        {!values.picture ? (
                          <p>Add Picture Here</p>
                        ) : (
                          <FlexBetween>
                            <Typography>{values.picture.name}</Typography>
                            <EditOutlinedIcon />
                          </FlexBetween>
                        )}
                      </Box>
                    )}
                  </DropZone>
                </Box>
              </>
            )}
            {/*This section is going to be used both for Register and login. */}
            <TextField
              label="Email"
              onBlue={handleBlur}
              onChange={handleChange}
              value={values.email}
              name="email"
              error={Boolean(touched.email) && Boolean(errors.email)}
              helperText={touched.email && errors.email}
              sx={{ gridColumn: "span 4" }}
            />
            <TextField
              label="Password"
              type="password"
              onBlue={handleBlur}
              onChange={handleChange}
              value={values.password}
              name="password"
              error={Boolean(touched.password) && Boolean(errors.email)}
              helperText={touched.password && errors.password}
              sx={{ gridColumn: "span 4" }}
            />
          </Box>
          {/* BUTTONS */}
          <Box>
            <Button
                fullWidth
                type="submit"
                sx={{
                    m:"2rem 0",
                    p: "1rem",
                    backgroundColor : palette.primary.main,
                    color:palette.background.alt,
                    "&:hover":{color:palette.primary.main},
                }}
            >
                {isLogin ? "LOGIN" : "REGISTER"}
            </Button>
            <Typography
                onClick={()=>{
                    setPageType(isLogin?"register":"login");
                    resetForm();
                }}
                sx={{
                    textDecoration: "underline",
                    color : palette.primary.main,
                    "&:hover" :{
                        cursor : "pointer",
                        color : palette.primary.light,
                    },              
                }}
            >
                {isLogin ? "Don't have an account? Sign up here.":"Already have an account? Login here."}
            </Typography>
          </Box>
        </form>
      )}
    </Formik>
  );
};

export default Form;
